// 初始化旋转木马
function initCarousel() {
    const itemCount = 12; // 旋转木马上的项目数量
    
    // 根据屏幕宽度动态调整大小
    const screenWidth = window.innerWidth;
    let radius, itemSize, imgSize, offset, yOffset;
    
    if (screenWidth <= 480) {
        // 小屏幕设备
        radius = 108; // 进一步缩小
        itemSize = 64.8; // 进一步缩小
        imgSize = 54; // 进一步缩小
        offset = 32.4; // 进一步缩小
        yOffset = 72; // 进一步缩小
    } else {
        // 大屏幕设备
        radius = 135; // 缩小为原来的0.9倍
        itemSize = 81; // 缩小为原来的0.9倍
        imgSize = 67.5; // 缩小为原来的0.9倍
        offset = 40.5; // 缩小为原来的0.9倍
        yOffset = 90; // 缩小为原来的0.9倍
    }
    
    for (let i = 0; i < itemCount; i++) {
        const angle = (i / itemCount) * Math.PI * 2;
        const x = Math.cos(angle) * radius;
        const z = Math.sin(angle) * radius;
        
        // 后面的图片高出前面的图片
        // z值越小（后面），y值越小（越高）
        const y = (yOffset * z) / (2 * radius);
        
        const item = document.createElement('div');
        item.className = 'carousel-item';
        item.style.position = 'absolute';
        item.style.left = `calc(50% - ${offset}px)`;
        item.style.top = `calc(50% - ${offset}px)`;
        item.style.width = `${itemSize}px`;
        item.style.height = `${itemSize}px`;
        item.style.transform = `translateX(${x}px) translateY(${y}px) translateZ(${z}px)`;
        item.style.zIndex = Math.floor(z + radius); // 根据z轴位置设置层级
        item.style.transition = 'transform 0.05s ease-out'; // 设置平滑过渡
        
        // 创建图片元素
        const img = document.createElement('img');
        img.src = carouselImages[i % carouselImages.length];
        img.style.width = `${imgSize}px !important`; // 添加!important提高优先级
        img.style.height = `${imgSize}px !important`; // 添加!important提高优先级
        img.style.borderRadius = '50%';
        img.style.objectFit = 'cover';
        img.style.display = 'block';
        img.style.margin = '0 auto';
        
        item.appendChild(img);
        carousel.appendChild(item);
    }
}

